package loopsinjava;

public class ForLoopInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int s = 0; s < 5; s++)

		{
			System.out.println(s + " \"For Loop\" in Java ");
		}

		for (int a = 0; a < 100; a++)

		{
			if ((a % 2) == 0) {
				System.out.println(a+ " is Even");
			} else {
				System.out.println(a+ " is Odd");
			}
		}

	}

}
