package loopsinjava;

public class WhileLoopInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		while(i<5)
		{
			System.out.println("Hello");
			i++;
		}
		
		int x[]= {45, 56, 67};
		
		int p=0;
		while(p<x.length)
		{
			System.out.println(x[p]);
			p++;
		}

	}

}
